class TestFindLargestInteger():
    def test_empty_lists(self):
        lst = [[]]
        assert(find_largest_integer(lst) == None)
    def test_different_data_types(self):
        lst = [[1, 2, 3], ['a', 'b', 'c'], [True, False]]
        assert(find_largest_integer(lst) == 3)
    def test_negative_numbers(self):
        lst = [[-5, -10, -3], [-2, -7, -1], [-8, -4, -6]]
        assert(find_largest_integer(lst) ==  -1)

def find_largest_integer(list_of_lists):
    max_number = float('-inf')  # Initialize with negative infinity
    for sublist in list_of_lists:
        for number in sublist:
            if number > max_number:
                max_number = number
    return max_number

def find_largest_integer(lst):
    largest_integer = None
    for sublist in lst:
        for item in sublist:
            if isinstance(item, int):
                if largest_integer is None or item > largest_integer:
                    largest_integer = item
    return largest_integer
